CREATE TABLE users (id_no INT AUTO_INCREMENT PRIMARY KEY, firstname VARCHAR(20) NOT NULL, lastname VARCHAR(20), email VARCHAR(50), phone VARCHAR(15), username VARCHAR(20) NOT NULL, PASSWORD VARCHAR(256) NOT NULL, state INT, local_government INT, FOREIGN KEY (state) REFERENCES states (id_no), FOREIGN KEY (local_government) REFERENCES local_govts (id_no) ON UPDATE CASCADE ON DELETE SET NULL)
